by_keyword.map:
function(doc)
{
  var tokens;

  for (var i in doc)
  {
    if (doc[i].title)
    {
      tokens = doc[i].title.split(/[^A-Z0-9\\-_]+/i);

      for (var word in tokens)
      {
        emit( tokens[word], null);
      }
    }
    if (doc[i].abstract)
    {
      tokens = doc[i].abstract.split(/[^A-Z0-9\\-_]+/i);

      for (var word in tokens)
      {
        emit(tokens[word], null);
      }
    }
    if (doc[i].description)
    {
      tokens = doc[i].description.split(/[^A-Z0-9\\-_]+/i);

      for (var word in tokens)
      {
        emit(tokens[word], null);
      }
    }
  }
}

